INSERT INTO `nv4_vi_about` VALUES (31,'Về chúng tôi','ve-chung-toi','','',0,'','<strong>Chung Phát Hưng Yên</strong>&nbsp;được thành lập từ năm 2005 với tổng diện tích mặt bằng rộng lớn. Công ty được đặt tại ví trí đắc địa, nằm ngay trên trục đường quốc lộ 5, gần trung tâm Hà Nội và bến cảng Hải Phỏng lên rất&nbsp;thuận lợi giao thông cho vận chuyển hàng hóa nội địa&nbsp;cũng như xuất khẩu.<br />\r\n<strong>Chung Phát Hưng Yên</strong>&nbsp;là doanh nghiệp chuyên sản xuất in ấn, thiết kế bao bì trên nhiều chất liệu và chủng loại sản phẩm khác nhau như: hộp màu&nbsp;Offset, hộp màu&nbsp;Offset có bồi sóng, hộp cứng, hộp PET, hộp cửa sổ, tờ rơi, túi sách, hộp carton...với nhiều năm kinh nghiệm. Chung Phát hiểu rằng bao bì bên ngoài không chỉ đựng và bảo vệ sản phẩm an toàn tới tay khách hàng mà còn là một trong những công cụ nhận diện, truyền tải nội dung và marketing thương hiệu không thể thiếu trong việc nhận diện thương hiệu hoàn hảo. Màu&nbsp;sắc, logo, thiết kế, … các chi tiết in ấn sắc nét sẽ thu hút người tiêu dùng.<br />\r\nVới trang thiết bị hiện đại được nhập khẩu từ Đức, Nhật, Đài Loan, Trung Quốc kết hợp với trình độ chuyên môn cao trong việc thiết kế tiêu chuẩn hóa bao bì chất lượng cao. Chung Phát sẽ giúp doanh nghiệp của bạn phát huy được hết chiến lược marketing và thông điệp bạn muốn truyền tải thông qua bao gói sản phẩm của bạn. Ngoài ra các sản phẩm từ giấy có thể tái chế được góp phần bảo vệ môi trường, thân thiện và an toàn sức khỏe được những nhà yêu môi trường khuyến khích sử dụng.&nbsp;<br />\r\nChúng tôi tin rằng &quot;Giá trị cốt lõi của thùng hàng là những sản phẩm đựng bên trong, các sản phẩm bao bì của chúng tôi giúp bạn thể hiện được tất cả những giá trị cốt lõi đó với khách hàng của bạn!&quot;','',1,'4','',1,1,1649902631,1649902631,1,13,0);
